import "./App.css";
import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { ThemeProvider } from "./ThemeContext";
import Navbar from "./Pages/Home/Navbar";
import Home from "./Pages/Home/Homescreen";

// âœ… Floating background text component
function FloatingBackground() {
  return (
    <div className="floating-tech-bg">
      <span className="tech floating1">React</span>
      <span className="tech floating2">Node.js</span>
      <span className="tech floating3">TypeScript</span>
      <span className="tech floating4">HTML</span>
      <span className="tech floating5">JavaScript</span>
      <span className="tech floating6">Python</span>
      <span className="tech floating7">CSS</span>
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <div className="App">
        <FloatingBackground /> {/* ðŸ‘ˆ Background effect added here */}
        <Router>
          <Navbar />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="*" element={<div>404 Not Found</div>} />
          </Routes>
        </Router>
      </div>
    </ThemeProvider>
  );
}

export default App;
