import { useRef, useState } from "react";

export default function ContactMe() {
  const form = useRef();
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData(form.current);

    try {
      const res = await fetch("https://getform.io/f/bgdlzkoa", {
        method: "POST",
        body: formData,
      });

      if (res.ok) {
        setSubmitted(true);
        form.current.reset();
      } else {
        alert("Submission failed. Please try again.");
      }
    } catch (error) {
      alert("Something went wrong. Try again later.");
    }
  };

  return (
    <section id="Contact" className="contact--section">
      <div>
        <p className="sub--title">Get In Touch</p>
        <h2>Contact Me</h2>
        <p className="text-lg">
          Feel free to reach out using the form below. I’ll respond as soon as possible!
        </p>
      </div>

      {submitted ? (
        <p className="text-lg">✅ Thank you! Your message has been sent.</p>
      ) : (
        <form ref={form} onSubmit={handleSubmit} className="contact--form--container">
          <div className="container">
            <label className="contact--label">
              <span className="text-md">First Name</span>
              <input type="text" name="first_name" className="contact--input text-md" required />
            </label>
            <label className="contact--label">
              <span className="text-md">Last Name</span>
              <input type="text" name="last_name" className="contact--input text-md" required />
            </label>
            <label className="contact--label">
              <span className="text-md">Email</span>
              <input type="email" name="email" className="contact--input text-md" required />
            </label>
            <label className="contact--label">
              <span className="text-md">Phone Number</span>
              <input type="tel" name="phone_number" className="contact--input text-md" required />
            </label>
          </div>

          <label className="contact--label">
            <span className="text-md">Message</span>
            <textarea
              name="message"
              className="contact--input text-md"
              rows="8"
              placeholder="Type your message..."
              required
            />
          </label>

          <label className="checkbox--label">
            <input type="checkbox" name="terms" required />
            <span className="text-sm">I accept the terms</span>
          </label>

          <div>
            <button type="submit" className="btn btn-primary contact--form--btn">
              Submit
            </button>
          </div>
        </form>
      )}
    </section>
  );
}
