import { useState, useEffect, useContext } from "react";
import { Link } from "react-scroll";
import { ThemeContext } from "../../ThemeContext"; // ✅ Adjust path if needed

function Navbar() {
  const [navActive, setNavActive] = useState(false);
  const { theme, toggleTheme } = useContext(ThemeContext);

  const toggleNav = () => {
    setNavActive(!navActive);
  };

  const closeMenu = () => {
    setNavActive(false);
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth <= 500) {
        closeMenu();
      }
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    if (window.innerWidth <= 1200) {
      closeMenu();
    }
  }, []);

  return (
    <nav className={`navbar ${navActive ? "active" : ""}`}>
      <div className="logo-container">
        <img src="./img/F.png" alt="Logo" className="logo-image" />
      </div>

      <button
        className={`nav__hamburger ${navActive ? "active" : ""}`}
        onClick={toggleNav}
        aria-label="Toggle navigation menu"
      >
        <span className="nav__hamburger__line"></span>
        <span className="nav__hamburger__line"></span>
        <span className="nav__hamburger__line"></span>
      </button>

      <div className={`navbar--items ${navActive ? "active" : ""}`}>
        <ul>
          <li>
            <Link onClick={closeMenu} activeClass="navbar--active-content" spy smooth offset={-70} duration={500} to="heroSection" className="navbar--content">Home</Link>
          </li>
          <li>
            <Link onClick={closeMenu} activeClass="navbar--active-content" spy smooth offset={-70} duration={500} to="Certifications" className="navbar--content">Certifications</Link>
          </li>
          <li>
            <Link onClick={closeMenu} activeClass="navbar--active-content" spy smooth offset={-70} duration={500} to="mySkills" className="navbar--content">My Expertise</Link>
          </li>
          <li>
            <Link onClick={closeMenu} activeClass="navbar--active-content" spy smooth offset={-70} duration={500} to="AboutMe" className="navbar--content">About Me</Link>
          </li>
          <li>
            <Link onClick={closeMenu} activeClass="navbar--active-content" spy smooth offset={-70} duration={500} to="MyPortfolio" className="navbar--content">Portfolio</Link>
          </li>
          <li>
            <Link onClick={closeMenu} activeClass="navbar--active-content" spy smooth offset={-70} duration={500} to="myintrest" className="navbar--content">My Achievements</Link>
          </li>
        </ul>
      </div>

      <Link onClick={closeMenu} activeClass="navbar--active-content" spy smooth offset={-70} duration={500} to="Contact" className="btn btn-outline-primary">Contact Me</Link>

      <button onClick={toggleTheme} className="theme-toggle-btn">
        {theme === "dark" ? "☀️ Light" : "🌙 Dark"}
      </button>
    </nav>
  );
}

export default Navbar;
