import { useState, useEffect, useRef } from "react";
import data from "../../data/index.json";
export default function MyAchievements() {
  const [fullscreenImage, setFullscreenImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [sectionVisible, setSectionVisible] = useState(false);
  const sectionRef = useRef();

  // Lazy-render section when visible
  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setSectionVisible(true);
          observer.disconnect();
        }
      },
      { threshold: 0.1 }
    );

    if (sectionRef.current) observer.observe(sectionRef.current);
    return () => observer.disconnect();
  }, []);

  // Preload all images once component is mounted
  useEffect(() => {
    if (sectionVisible) {
      data?.achievements?.forEach((item) => {
        const img = new Image();
        img.src = item.src;
      });
    }
  }, [sectionVisible]);

  const handleImageClick = (src) => {
    setLoading(true);
    setFullscreenImage(src);
  };

  const handleImageLoad = () => {
    setLoading(false);
  };

  return (
    <section
      className="achievements--section"
      id="myAchievements"
      ref={sectionRef}
    >
      {sectionVisible && (
        <>
          <div className="achievements--container-box">
            <div className="achievements--container">
              <h2 className="skills--section--heading">My Achievements</h2>
            </div>
          </div>

          <div className="achievements--section--container">
            {data?.achievements?.map((item, index) => (
              <div key={index} className="achievements--section--card">
                <div
                  className="achievements--section--img"
                  onClick={() => handleImageClick(item.src)}
                  style={{ cursor: "zoom-in" }}
                >
                  <img
                    src={item.src}
                    alt={item.title}
                    loading="lazy"
                    width="100%"
                    height="280"
                    style={{
                      objectFit: "cover",
                      borderRadius: "8px",
                      transition: "transform 0.3s ease",
                    }}
                  />
                </div>
                <div className="achievements--section--card--content">
                  <h3 className="achievements--section--title">{item.title}</h3>
                  <p className="achievements--section--description">
                    {item.description}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </>
      )}

      {fullscreenImage && (
        <div
          className="fullscreen--overlay"
          onClick={() => setFullscreenImage(null)}
        >
          {loading && <div className="fullscreen--loader">Loading...</div>}
          <img
            src={fullscreenImage}
            alt="Fullscreen"
            onLoad={handleImageLoad}
            style={{
              maxHeight: "90%",
              maxWidth: "90%",
              objectFit: "contain",
              transition: "opacity 0.3s ease",
              opacity: loading ? 0 : 1,
            }}
          />
        </div>
      )}
    </section>
  );
}
