import data from "../../data/index.json";
import "./Certification.scss";

// CDN logos mapped by certificate title or issuer
const logoMap = {
  "Meta": "https://cdn.worldvectorlogo.com/logos/meta-1.svg",
  "University of Michigan": "https://cdn.worldvectorlogo.com/logos/university-of-michigan.svg",
  "Duke University": "https://cdn.worldvectorlogo.com/logos/duke-university.svg",
  "MongoDB": "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mongodb/mongodb-original.svg",
  "LinkedIn Learning": "https://cdn.worldvectorlogo.com/logos/linkedin-icon-2.svg",
  "freeCodeCamp": "https://cdn.worldvectorlogo.com/logos/freecodecamp-1.svg",
  "Udemy": "https://cdn.worldvectorlogo.com/logos/udemy-2.svg",
  "Microsoft Learn": "https://cdn.worldvectorlogo.com/logos/microsoft-6.svg"
};

// Helper to get the logo by matching issuer or part of title
const getLogo = (title, issuer) => {
  const keys = Object.keys(logoMap);
  for (const key of keys) {
    if (
      (issuer && issuer.toLowerCase().includes(key.toLowerCase())) ||
      (title && title.toLowerCase().includes(key.toLowerCase()))
    ) {
      return logoMap[key];
    }
  }
  return null; // default or fallback logo can go here
};

export default function Certifications() {
  return (
    <section className="certifications--section" id="certifications">
      <div className="portfolio--container">
        <p className="section--title">Certifications</p>
        <h2 className="certifications--section--heading">My Achievements</h2>
      </div>

      <div className="certifications--section--container">
        {data?.certifications?.map((item, index) => {
          const logo = getLogo(item.title, item.issuer);
          return (
            <div key={index} className="certifications--section--card">
              <div className="certifications--section--img">
                <img src={item.image} alt={item.title} />
              </div>
              <div className="certifications--section--card--content">
                {logo && (
                  <img
                    className="certifications--section--logo"
                    src={logo}
                    alt={`${item.issuer} logo`}
                  />
                )}
                <h3 className="certifications--section--title">{item.title}</h3>
                <p className="certifications--section--issuer">{item.issuer}</p>
                {item.link && (
                  <a
                    className="certifications--section--link"
                    href={item.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    View Certificate
                  </a>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </section>
  );
}
