import React, { useState } from "react";

function TechReveal() {
  const [pos, setPos] = useState({ x: -9999, y: -9999 }); // hide by default

  const handleMouseMove = (e) => {
    setPos({ x: e.clientX, y: e.clientY });
  };

  return (
    <div className="tech-reveal-wrapper" onMouseMove={handleMouseMove}>
      <div className="tech-grid">
        {/* Repeated static tech words in background */}
        {Array.from({ length: 200 }, (_, i) => (
          <span key={i} className="tech-word">
            {["Java", "JS", "React", "Python", "C++", "Node", "HTML", "CSS"][i % 8]}
          </span>
        ))}
      </div>

      <div
        className="reveal-mask"
        style={{
          top: pos.y - 100,
          left: pos.x - 100,
        }}
      />
    </div>
  );
}

export default TechReveal;
