import { useState } from "react";

export default function ContactMe() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phoneNumber: "",
    message: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleGmailCompose = (e) => {
    e.preventDefault();

    const subject = `Contact from ${formData.firstName} ${formData.lastName}`;
    const body = `Name: ${formData.firstName} ${formData.lastName}%0AEmail: ${formData.email}%0APhone: ${formData.phoneNumber}%0A%0AMessage:%0A${formData.message}`;

    const gmailURL = `https://mail.google.com/mail/?view=cm&fs=1&to=your_email@example.com&su=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;

    window.open(gmailURL, "_blank");
  };

  return (
    <section id="Contact" className="contact--section">
      <div>
        <p className="sub--title">Get In Touch</p>
        <h2>Contact Me</h2>
        <p className="text-lg">Feel free to reach out using the form below.</p>
      </div>
      <form onSubmit={handleGmailCompose} className="contact--form--container">
        <div className="container">
          <label className="contact--label">
            <span className="text-md">First Name</span>
            <input type="text" name="firstName" className="contact--input text-md" value={formData.firstName} onChange={handleChange} required />
          </label>
          <label className="contact--label">
            <span className="text-md">Last Name</span>
            <input type="text" name="lastName" className="contact--input text-md" value={formData.lastName} onChange={handleChange} required />
          </label>
          <label className="contact--label">
            <span className="text-md">Email</span>
            <input type="email" name="email" className="contact--input text-md" value={formData.email} onChange={handleChange} required />
          </label>
          <label className="contact--label">
            <span className="text-md">Phone Number</span>
            <input type="text" name="phoneNumber" className="contact--input text-md" value={formData.phoneNumber} onChange={handleChange} required />
          </label>
        </div>

        <label className="contact--label">
          <span className="text-md">Message</span>
          <textarea name="message" className="contact--input text-md" rows="8" value={formData.message} onChange={handleChange} required />
        </label>

        <label className="checkbox--label">
          <input type="checkbox" required name="terms" />
          <span className="text-sm">I accept the terms</span>
        </label>

        <div>
          <button type="submit" className="btn btn-primary contact--form--btn">Open Gmail</button>
        </div>
      </form>
    </section>
  );
}
