import React, { useState } from "react";
import "./ChatbotUI.css";

const ChatbotUI = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { type: "bot", text: "Hi! Ask me anything about my portfolio." },
  ]);
  const [input, setInput] = useState("");

  const toggleChat = () => setIsOpen(!isOpen);

  const sendMessage = () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { type: "user", text: input }];

    let reply = "I'm here to help!";

    if (input.toLowerCase().includes("project")) {
      reply = "Check out my Result Analytics Dashboard and other React apps!";
    } else if (input.toLowerCase().includes("skills")) {
      reply = "I work with React.js, MongoDB, Node.js, and Python.";
    } else if (input.toLowerCase().includes("contact")) {
      reply = "You can contact me via the form on the Contact section!";
    }

    setMessages([...newMessages, { type: "bot", text: reply }]);
    setInput("");
  };

  return (
    <div className="chatbot-container">
      {!isOpen && (
        <button className="chat-toggle-button" onClick={toggleChat}>
          ðŸ’¬
        </button>
      )}
      {isOpen && (
        <div className="chat-window-wrapper">
          <div style={{ display: "flex", justifyContent: "space-between", padding: "10px", borderBottom: "1px solid #ccc" }}>
            <strong>Portfolio Chatbot</strong>
            <button onClick={toggleChat} style={{ background: "none", border: "none", fontSize: "18px" }}>Ã—</button>
          </div>

          <div className="chat-window">
            {messages.map((msg, index) => (
              <div key={index} className={`message ${msg.type}`}>
                {msg.text}
              </div>
            ))}
          </div>

          <div className="input-row">
            <input
              type="text"
              placeholder="Ask something..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatbotUI;
