import React, { useEffect, useState } from "react";

const techWords = [
  "Java",
  "Python",
  "JavaScript",
  "C++",
  "TypeScript",
  "React",
  "Node.js",
  "HTML",
  "CSS"
];

function FloatingEffect() {
  const [effects, setEffects] = useState([]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      const word = techWords[Math.floor(Math.random() * techWords.length)];
      const newEffect = {
        id: Date.now() + Math.random(),
        x: e.clientX,
        y: e.clientY,
        text: word,
      };

      setEffects((prev) => [...prev, newEffect]);

      setTimeout(() => {
        setEffects((prev) => prev.filter((effect) => effect.id !== newEffect.id));
      }, 1000); // remove after 1s
    };

    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  return (
    <div className="floating-cursor-layer">
      {effects.map((effect) => (
        <span
          key={effect.id}
          className="floating-text"
          style={{ top: effect.y, left: effect.x }}
        >
          {effect.text}
        </span>
      ))}
    </div>
  );
}

export default FloatingEffect;
