import React, { useEffect, useRef } from "react";
import data from "../../data/index.json";
import "../../App.css"; // ✅ Ensure CSS with animation classes is imported

export default function Certifications() {
  const containerRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("show");
            observer.unobserve(entry.target); // Observe once
          }
        });
      },
      {
        threshold: 0.1,
      }
    );

    const cards = containerRef.current?.querySelectorAll(".certifications--section--card") || [];
    cards.forEach((card) => observer.observe(card));

    return () => {
      cards.forEach((card) => observer.unobserve(card));
    };
  }, []);

  return (
    <section className="certifications--section" id="Certifications">
      <div className="portfolio--container">
        <h2 className="skills--section--heading">Certifications</h2>
      </div>

      <div className="certifications--section--container" ref={containerRef}>
        {data?.certifications.map((item, index) => (
          <div
            key={index}
            className="certifications--section--card"
            style={{ transitionDelay: `${index * 100}ms` }} // Optional: staggered appearance
          >
            <div className="certifications--section--img">
              <img src={item.src} alt={item.title} />
            </div>
            <div className="certifications--section--card--content">
              <h3 className="certifications--section--title">{item.title}</h3>
              <p className="certifications--section--description">{item.description}</p>
              {item.link && (
                <a
                  href={item.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-primary"
                >
                  View Certificate →
                </a>
              )}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
