import React, { useEffect, useRef } from "react";

const techWords = [
  "Java", "Python", "JavaScript", "React", "Node.js", "C++", "TypeScript", "HTML", "CSS",
];

export default function TechReveal() {
  const wrapperRef = useRef(null);

  useEffect(() => {
    const updateMaskPosition = (e) => {
      if (wrapperRef.current) {
        wrapperRef.current.style.setProperty("--x", `${e.clientX}px`);
        wrapperRef.current.style.setProperty("--y", `${e.clientY}px`);
      }
    };

    window.addEventListener("mousemove", updateMaskPosition);
    return () => window.removeEventListener("mousemove", updateMaskPosition);
  }, []);

  return (
    <div className="tech-reveal-layer" ref={wrapperRef}>
      {Array.from({ length: 250 }, (_, i) => (
        <span key={i} className="tech-reveal-text">
          {techWords[i % techWords.length]}
        </span>
      ))}
    </div>
  );
}
